import 'package:flutter/material.dart';
import 'webview_screen.dart';
import 'courses_screen.dart';

class PostLoginChoiceScreen extends StatelessWidget {
  final String baseUrl;
  final String token; // token acquired after login

  const PostLoginChoiceScreen({
    Key? key,
    required this.baseUrl,
    required this.token,
  }) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Choose Option'),
      ),
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            ElevatedButton(
              onPressed: () {
                // Open the URL in a WebView.
                Navigator.push(
                  context,
                  MaterialPageRoute(
                    builder: (_) => WebViewScreen(url: baseUrl),
                  ),
                );
              },
              child: const Text('Open Site in Browser'),
            ),
            const SizedBox(height: 20),
            ElevatedButton(
              onPressed: () {
                // Stay in the app and show the courses screen.
                Navigator.push(
                  context,
                  MaterialPageRoute(
                    builder: (_) =>
                        CoursesScreen(baseUrl: baseUrl, token: token),
                  ),
                );
              },
              child: const Text('Use App'),
            ),
          ],
        ),
      ),
    );
  }
}
