import 'package:flutter/material.dart';
import 'package:dio/dio.dart';
import 'discussion_posts_screen.dart';

class ForumDiscussionsScreen extends StatefulWidget {
  final String baseUrl;
  final String token;
  final int forumId;

  const ForumDiscussionsScreen({
    Key? key,
    required this.baseUrl,
    required this.token,
    required this.forumId,
  }) : super(key: key);

  @override
  State<ForumDiscussionsScreen> createState() => _ForumDiscussionsScreenState();
}

class _ForumDiscussionsScreenState extends State<ForumDiscussionsScreen> {
  bool _isLoading = true;
  List _discussions = [];
  final Dio _dio = Dio();

  @override
  void initState() {
    super.initState();
    fetchDiscussions();
  }

  Future<void> fetchDiscussions() async {
    final String serverUrl = '${widget.baseUrl}/webservice/rest/server.php';
    try {
      final response = await _dio.get(
        serverUrl,
        queryParameters: {
          'wstoken': widget.token,
          'wsfunction': 'mod_forum_get_forum_discussions',
          'moodlewsrestformat': 'json',
          'forumid': widget.forumId,
        },
      );
      setState(() {
        _discussions = response.data['discussions'] ?? [];
        _isLoading = false;
      });
    } catch (e) {
      print('Error fetching discussions: $e');
      setState(() {
        _isLoading = false;
      });
    }
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Forum Discussions'),
      ),
      body: _isLoading
          ? const Center(child: CircularProgressIndicator())
          : _discussions.isEmpty
              ? const Center(child: Text('No discussions found.'))
              : ListView.builder(
                  itemCount: _discussions.length,
                  itemBuilder: (context, index) {
                    final discussion = _discussions[index];
                    return ListTile(
                      title: Text(module['name'] ?? 'Unnamed Module'),
                      subtitle: Text('Type: ${module['modname']}, Visible: ${module['visible']}'),
                      onTap: () {
                        if (module['modname'] == 'forum') {
                          // Try forum-specific handling.
                          Navigator.push(
                            context,
                            MaterialPageRoute(
                              builder: (_) => ForumDiscussionsScreen(
                                baseUrl: widget.baseUrl,
                                token: widget.token,
                                forumId: module['instance'],
                              ),
                            ),
                          );
                        } else {
                          // For other types, open a generic detail screen.
                          Navigator.push(
                            context,
                            MaterialPageRoute(
                              builder: (_) => ModuleDetailScreen(module: module),
                            ),
                          );
                        }
                      },
                    );

                  },
                ),
    );
  }
}
